const now = new Date();
const dow = now.getDay(); // –î–µ–Ω—å –Ω–µ–¥–µ–ª–∏: –≤—Å=0, –ø–Ω=1, ... —Å–±=6

// –î–∞—Ç–∞ –æ–∫–æ–Ω—á–∞–Ω–∏—è –ø—Ä–µ–¥—ã–¥—É—â–µ–π –Ω–µ–¥–µ–ª–∏: –≤–æ—Å–∫—Ä–µ—Å–µ–Ω—å–µ
// (—É n8n date.getDay() –≤—Å–µ–≥–¥–∞ –≤—Å=0, –ø–Ω=1 ...)
const end = new Date(now.getFullYear(), now.getMonth(), now.getDate() - dow); // –í–°–ö –ø—Ä–µ–¥—ã–¥—É—â–µ–π –Ω–µ–¥–µ–ª–∏
const start = new Date(end.getFullYear(), end.getMonth(), end.getDate() - 6);

function pad(n) { return n < 10 ? '0' + n : n; }

const startStr = `${pad(start.getDate())}.${pad(start.getMonth()+1)}.${start.getFullYear()}`;
const endStr = `${pad(end.getDate())}.${pad(end.getMonth()+1)}.${end.getFullYear()}`;

return { period: `${startStr} - ${endStr}` };
