// –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞–ª–∏—á–∏–µ –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
const userNode = $('Get User for VM Creation');
if (!userNode || userNode.all().length === 0) {
  return [{
    json: {
      canCreate: false,
      currentCount: 0,
      limit: 0,
      error: 'User not found',
      chat_id: $('Edit Fields').item.json.chat_id
    },
    pairedItem: 0
  }];
}

const userData = userNode.first().json;
const allVMs = $input.all();

// –°—á–∏—Ç–∞–µ–º –í–ú —Ç–µ–∫—É—â–µ–≥–æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
const userVMs = allVMs.filter(vm => 
  parseInt(vm.json.user_id) === parseInt(userData.user_id)
);

const currentCount = userVMs.length;
const limit = parseInt(userData.license_limit) || 0;

return [{
  json: {
    canCreate: currentCount < limit,
    currentCount,
    limit,
    user_id: userData.user_id,
    telegram_id: userData.telegram_id,
    username: userData.username,
    chat_id: $('Edit Fields').item.json.chat_id
  },
  pairedItem: 0
}];

// –ë–µ–∑–æ–ø–∞—Å–Ω–∞—è –ø—Ä–æ–≤–µ—Ä–∫–∞: –æ—Ç–∫—É–¥–∞ –ø—Ä–∏—à–ª–∏ –¥–∞–Ω–Ω—ã–µ?
let userData = null;

try {
  const userNode = $('Get User for VM Creation');
  if (userNode && userNode.all().length > 0) {
    userData = userNode.first().json;
  }
} catch (e) {
  // –£–∑–µ–ª –Ω–µ –≤—ã–ø–æ–ª–Ω—è–ª—Å—è ‚Äî –∑–Ω–∞—á–∏—Ç –ø—Ä–∏—à–ª–∏ —á–µ—Ä–µ–∑ State Router
}

// –ï—Å–ª–∏ userData –Ω–µ—Ç ‚Äî –ø–æ–ª—É—á–∞–µ–º –∏–∑ State Router
if (!userData) {
  const stateData = $('Check User State').first().json;

  // –ü–∞—Ä—Å–∏–º –¥–∞–Ω–Ω—ã–µ –∏–∑ –ø–æ–ª—è data
  let parsedData = {};
  try {
    parsedData = typeof stateData.data === 'string' 
      ? JSON.parse(stateData.data) 
      : stateData.data || {};
  } catch (e) {
    console.error('–û—à–∏–±–∫–∞ –ø–∞—Ä—Å–∏–Ω–≥–∞ data:', e);
    parsedData = {};
  }

  // –û–ë–Ø–ó–ê–¢–ï–õ–¨–ù–û –±–µ—Ä—ë–º user_id –∏–∑ data, –ù–ï –∏–∑ telegram_id!
  if (!parsedData.user_id) {
    throw new Error('‚ùå –í —Å–æ—Å—Ç–æ—è–Ω–∏–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –Ω–µ—Ç user_id! –û–±—Ä–∞—Ç–∏—Ç–µ—Å—å –∫ –∞–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä—É.');
  }

  userData = {
    user_id: parsedData.user_id,
    telegram_id: stateData.telegram_id,
    username: parsedData.username || `User${parsedData.user_id}`
  };
}

// –ü–æ–ª—É—á–∞–µ–º –≤—Ö–æ–¥–Ω–æ–π —Ç–µ–∫—Å—Ç (–Ω–æ–º–µ—Ä–∞ –ü–û)
const userInput = $('Edit Fields').item.json.text;

// –ü–∞—Ä—Å–∏–º –≤—ã–±—Ä–∞–Ω–Ω—ã–µ ID –ü–û
let selectedSoftwareIds = [];

// –£–±–∏—Ä–∞–µ–º –∫–æ–º–∞–Ω–¥—É /createvm –µ—Å–ª–∏ –µ—Å—Ç—å
const cleanInput = userInput.replace(/^\/createvm\s*/i, '').trim();

if (cleanInput) {
  // –ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –≤–≤—ë–ª –Ω–æ–º–µ—Ä–∞
  selectedSoftwareIds = cleanInput.split(',')
    .map(id => parseInt(id.trim()))
    .filter(id => !isNaN(id));
}

// –ï—Å–ª–∏ –Ω–µ —É–∫–∞–∑–∞–Ω–æ –ü–û ‚Äî –æ—à–∏–±–∫–∞!
if (selectedSoftwareIds.length === 0) {
  throw new Error('–ù–µ —É–∫–∞–∑–∞–Ω–æ –ü–û –¥–ª—è —É—Å—Ç–∞–Ω–æ–≤–∫–∏! –í—ã–±–µ—Ä–∏—Ç–µ –∏–∑ –º–µ–Ω—é.');
}

// –ó–∞–≥—Ä—É–∂–∞–µ–º –∫–∞—Ç–∞–ª–æ–≥ –ü–û (–ë–ï–ó–û–ü–ê–°–ù–û!)
let softwareCatalog = [];
try {
  const catalogNode = $('Load Software Catalog').first();

  if (Array.isArray(catalogNode.json)) {
    softwareCatalog = catalogNode.json;
  } else {
    const allCatalog = $('Load Software Catalog').all();
    if (Array.isArray(allCatalog)) {
      softwareCatalog = allCatalog.map(item => item.json);
    }
  }
} catch (e) {
  console.error('–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∫–∞—Ç–∞–ª–æ–≥–∞:', e);
  softwareCatalog = [];
}

// –†–∞—Å—Å—á–∏—Ç—ã–≤–∞–µ–º —Å—Ç–æ–∏–º–æ—Å—Ç—å –∏ —Å–ø–∏—Å–æ–∫ –ü–û
let monthlyCost = 0;
const installedSoftware = [];

selectedSoftwareIds.forEach(id => {
  const sw = softwareCatalog.find(s => parseInt(s.software_id) === id);
  if (sw) {
    monthlyCost += parseInt(sw.cost_per_month);
    installedSoftware.push(sw.name);
  }
});

// –ü–æ–ª—É—á–∞–µ–º –≤—Å–µ —Å—É—â–µ—Å—Ç–≤—É—é—â–∏–µ –í–ú –¥–ª—è –≥–µ–Ω–µ—Ä–∞—Ü–∏–∏ –Ω–æ–≤–æ–≥–æ ID (–ë–ï–ó–û–ü–ê–°–ù–û!)
let allVMs = [];
try {
  const vmsNode = $('Load Software Catalog2');
  if (vmsNode && vmsNode.all) {
    const vmsData = vmsNode.all();
    if (Array.isArray(vmsData)) {
      allVMs = vmsData;
    }
  }
} catch (e) {
  allVMs = [];
}

// –ì–µ–Ω–µ—Ä–∏—Ä—É–µ–º –£–ù–ò–ö–ê–õ–¨–ù–´–ô vmid
// –ò—Å–ø–æ–ª—å–∑—É–µ–º timestamp + —Å–ª—É—á–∞–π–Ω–æ–µ —á–∏—Å–ª–æ –¥–ª—è –≥–∞—Ä–∞–Ω—Ç–∏–∏ —É–Ω–∏–∫–∞–ª—å–Ω–æ—Å—Ç–∏
const timestamp = Date.now();
const random = Math.floor(Math.random() * 1000);
const newVmId = parseInt(`${timestamp}${random}`.slice(-10)); // –ë–µ—Ä—ë–º –ø–æ—Å–ª–µ–¥–Ω–∏–µ 10 —Ü–∏—Ñ—Ä

// –ì–µ–Ω–µ—Ä–∏—Ä—É–µ–º –£–ù–ò–ö–ê–õ–¨–ù–û–ï –∏–º—è –í–ú
// –§–æ—Ä–º–∞—Ç: VM-[user_id]-[timestamp]-[–ü–û]
const shortTimestamp = timestamp.toString().slice(-6); // –ü–æ—Å–ª–µ–¥–Ω–∏–µ 6 —Ü–∏—Ñ—Ä
const softwareAbbr = installedSoftware.length > 0 
  ? installedSoftware[0].substring(0, 3).toUpperCase() 
  : 'SYS';
const vmName = `VM-${userData.user_id}-${shortTimestamp}-${softwareAbbr}`;

// –°–∏–º—É–ª–∏—Ä—É–µ–º –º–µ—Ç—Ä–∏–∫–∏
const usageHours = Math.floor(Math.random() * 36) + 5; // 5-40 —á–∞—Å–æ–≤
const cpuUsage = Math.floor(Math.random() * 70) + 15; // 15-85%
const ramUsage = Math.floor(Math.random() * 60) + 30; // 30-90%

let status = 'active';
if (usageHours < 5) status = 'stopped';
else if (usageHours < 20) status = 'idle';

const now = new Date();
const createdAt = now.toISOString().split('T')[0];

return {
  json: {
    vm_id: newVmId,
    user_id: userData.user_id,
    vm_name: vmName,
    software_installed: selectedSoftwareIds.join(','),
    created_at: createdAt,
    last_active: createdAt,
    cpu_usage_avg: cpuUsage,
    ram_usage_avg: ramUsage,
    status: status,
    monthly_cost: monthlyCost,
    usage_hours_week: usageHours,
    inactivity_days: 0,
    alert_sent: false,
    installed_software_names: installedSoftware.join(', '),
    chat_id: $('Edit Fields').item.json.chat_id
  }
};

const userData = $('Get User').first().json;
const allVMs = $('Get VMs for Resources').all();

// –§–∏–ª—å—Ç—Ä—É–µ–º –í–ú —Ç–µ–∫—É—â–µ–≥–æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
const userVMs = allVMs.filter(vm => vm.json.user_id === userData.user_id);

let totalCost = 0;
let activeCount = 0;
let idleCount = 0;
let stoppedCount = 0;
let activeCost = 0;
let idleCost = 0;
let stoppedCost = 0;

userVMs.forEach(vm => {
  totalCost += vm.json.monthly_cost;

  if (vm.json.status === 'active') {
    activeCount++;
    activeCost += vm.json.monthly_cost;
  } else if (vm.json.status === 'idle') {
    idleCount++;
    idleCost += vm.json.monthly_cost;
  } else {
    stoppedCount++;
    stoppedCost += vm.json.monthly_cost;
  }
});

const licenseMap = {
  basic: { name: 'Basic', limit: 3, cost: 5000 },
  standard: { name: 'Standard', limit: 10, cost: 15000 },
  premium: { name: 'Premium', limit: 50, cost: 40000 }
};

const license = licenseMap[userData.license_type];

let message = `üìä –í–∞—à–∏ —Ä–µ—Å—É—Ä—Å—ã\n‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n\n`;
message += `üë§ –ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å: ${userData.username}\n`;
message += `üíº –õ–∏—Ü–µ–Ω–∑–∏—è: ${license.name} (${userVMs.length}/${license.limit} –í–ú)\n`;
message += `üí∞ –ó–∞—Ç—Ä–∞—Ç—ã: ${totalCost.toLocaleString()}‚ÇΩ/–º–µ—Å\n\n`;

message += `üñ•Ô∏è –í–∏—Ä—Ç—É–∞–ª—å–Ω—ã–µ –º–∞—à–∏–Ω—ã:\n`;
message += `‚úÖ –ê–∫—Ç–∏–≤–Ω—ã—Ö: ${activeCount} (${activeCost.toLocaleString()}‚ÇΩ)\n`;
message += `‚ö†Ô∏è –ü—Ä–æ—Å—Ç–∞–∏–≤–∞—é—â–∏—Ö: ${idleCount} (${idleCost.toLocaleString()}‚ÇΩ)\n`;
message += `‚ùå –û—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–Ω—ã—Ö: ${stoppedCount} (${stoppedCost.toLocaleString()}‚ÇΩ)\n\n`;

// –î–û–ë–ê–í–õ–Ø–ï–ú –°–ü–ò–°–û–ö –í–ú
if (userVMs.length > 0) {
  message += `üìã –°–ø–∏—Å–æ–∫ –í–ú:\n`;
  userVMs.forEach((vm, index) => {
    const statusIcon = vm.json.status === 'active' ? '‚úÖ' : 
                       vm.json.status === 'idle' ? '‚ö†Ô∏è' : '‚ùå';
    message += `${index + 1}. ${statusIcon} ${vm.json.vm_name}\n`;
    message += `   üíø –ü–û: ${vm.json.software_installed}\n`;
    message += `   üí∞ ${vm.json.monthly_cost.toLocaleString()}‚ÇΩ/–º–µ—Å\n`;
    message += `   üîç /vmdetails ${vm.json.vm_id}\n\n`;
  });
}

message += `‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ‚îÅ\n`;
message += `üí° –ò—Å–ø–æ–ª—å–∑—É–π—Ç–µ:\n`;
message += `/optimize - –ü–æ–ª—É—á–∏—Ç—å —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏\n`;
message += `/checkidle - –ü—Ä–æ–≤–µ—Ä–∏—Ç—å –ø—Ä–æ—Å—Ç–∞–∏–≤–∞—é—â–∏–µ –í–ú`;

return {
  json: {
    message,
    chat_id: $('Webhook - Telegram Bot').item.json.message.chat.id
  }
};

// –ü–æ–ª—É—á–∞–µ–º –¥–∞–Ω–Ω—ã–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
const userData = $('Get User for Optimize').first().json;

// –ü–æ–ª—É—á–∞–µ–º –í–ú –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
const allVMs = $('Get VMs for Optimize').all();

// –ü–æ–ª—É—á–∞–µ–º –∫–∞—Ç–∞–ª–æ–≥ –ü–û
const software = $input.all();

// –§–∏–ª—å—Ç—Ä—É–µ–º –í–ú —Ç–µ–∫—É—â–µ–≥–æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
const userVMs = allVMs.filter(vm => vm.json.user_id === userData.user_id);

// –°–æ–∑–¥–∞—ë–º —Å–ø—Ä–∞–≤–æ—á–Ω–∏–∫ –ü–û
const softwareMap = {};
software.forEach(sw => {
  softwareMap[sw.json.software_id] = sw.json;
});

// –§–æ—Ä–º–∏—Ä—É–µ–º –¥–µ—Ç–∞–ª—å–Ω–æ–µ –æ–ø–∏—Å–∞–Ω–∏–µ –∫–∞–∂–¥–æ–π –í–ú
let vmDescriptions = '';
userVMs.forEach((vm, index) => {
  // –ë–ï–ó–û–ü–ê–°–ù–û –ø–∞—Ä—Å–∏–º software_installed (–º–æ–∂–µ—Ç –±—ã—Ç—å —á–∏—Å–ª–æ–º –∏–ª–∏ —Å—Ç—Ä–æ–∫–æ–π)
  let softwareIds = [];
  if (vm.json.software_installed) {
    const softwareStr = String(vm.json.software_installed);
    softwareIds = softwareStr.split(',').map(id => parseInt(id.trim())).filter(id => !isNaN(id));
  }

  const softwareNames = softwareIds.map(id => 
    softwareMap[id] ? softwareMap[id].name : 'Unknown'
  ).join(', ');

  vmDescriptions += `${index + 1}. ${vm.json.vm_name}\n`;
  vmDescriptions += `   –ü–û: ${softwareNames || '–ù–µ —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–æ'}\n`;
  vmDescriptions += `   –ò—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ: ${vm.json.usage_hours_week || 0} —á–∞—Å–æ–≤/–Ω–µ–¥–µ–ª—é\n`;
  vmDescriptions += `   CPU: ${vm.json.cpu_usage_avg || 0}%, RAM: ${vm.json.ram_usage_avg || 0}%\n`;
  vmDescriptions += `   –ü—Ä–æ—Å—Ç–æ–π: ${vm.json.inactivity_days || 0} –¥–Ω–µ–π\n`;
  vmDescriptions += `   –°—Ç–æ–∏–º–æ—Å—Ç—å: ${vm.json.monthly_cost || 0}‚ÇΩ/–º–µ—Å\n`;
  vmDescriptions += `   –°—Ç–∞—Ç—É—Å: ${vm.json.status}\n\n`;
});

// –ï—Å–ª–∏ –Ω–µ—Ç –í–ú
if (userVMs.length === 0) {
  return {
    json: {
      error: true,
      message: '‚ùå –£ –≤–∞—Å –Ω–µ—Ç –≤–∏—Ä—Ç—É–∞–ª—å–Ω—ã—Ö –º–∞—à–∏–Ω –¥–ª—è –∞–Ω–∞–ª–∏–∑–∞\n\n–°–æ–∑–¥–∞–π—Ç–µ –í–ú –∫–æ–º–∞–Ω–¥–æ–π /createvm',
      chat_id: $('Edit Fields').first().json.chat_id
    }
  };
}

const totalCost = userVMs.reduce((sum, vm) => sum + (vm.json.monthly_cost || 0), 0);

const prompt = `–ü—Ä–æ–∞–Ω–∞–ª–∏–∑–∏—Ä—É–π –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ –≤–∏—Ä—Ç—É–∞–ª—å–Ω—ã—Ö –º–∞—à–∏–Ω –∏ –¥–∞–π –∫–æ–Ω–∫—Ä–µ—Ç–Ω—ã–µ —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏.

üìä –î–∞–Ω–Ω—ã–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è:
- –õ–∏—Ü–µ–Ω–∑–∏—è: ${userData.license_type} (${userVMs.length}/${userData.license_limit} –í–ú)
- –¢–µ–∫—É—â–∏–µ –∑–∞—Ç—Ä–∞—Ç—ã: ${totalCost}‚ÇΩ/–º–µ—Å

üñ•Ô∏è –í–∏—Ä—Ç—É–∞–ª—å–Ω—ã–µ –º–∞—à–∏–Ω—ã:
${vmDescriptions}

üìã –ó–∞–¥–∞—á–∏:
1. –û–ø—Ä–µ–¥–µ–ª–∏ –í–ú –¥–ª—è —É–¥–∞–ª–µ–Ω–∏—è (–∫—Ä–∏—Ç–µ—Ä–∏–π: –ø—Ä–æ—Å—Ç–æ–π >14 –¥–Ω–µ–π –ò–õ–ò usage_hours_week <5)
2. –ù–∞–π–¥–∏ –í–ú –¥–ª—è –æ–±—ä–µ–¥–∏–Ω–µ–Ω–∏—è (–∫—Ä–∏—Ç–µ—Ä–∏–π: –ø–æ—Ö–æ–∂–∏–π –Ω–∞–±–æ—Ä –ü–û –ò —Å—É–º–º–∞—Ä–Ω–∞—è –Ω–∞–≥—Ä—É–∑–∫–∞ CPU+RAM <140%)
3. –û—Ü–µ–Ω–∏ –æ–ø—Ç–∏–º–∏–∑–∞—Ü–∏—é –ª–∏—Ü–µ–Ω–∑–∏–∏:
   - basic: 3 –í–ú, 5000‚ÇΩ/–º–µ—Å
   - standard: 10 –í–ú, 15000‚ÇΩ/–º–µ—Å
   - premium: 50 –í–ú, 40000‚ÇΩ/–º–µ—Å
4. –†–∞—Å—Å—á–∏—Ç–∞–π –ø–æ—Ç–µ–Ω—Ü–∏–∞–ª—å–Ω—É—é —ç–∫–æ–Ω–æ–º–∏—é

–í–µ—Ä–Ω–∏ –æ—Ç–≤–µ—Ç –°–¢–†–û–ì–û –ø–æ —Å—Ç—Ä—É–∫—Ç—É—Ä–µ –∏–∑ —Å–∏—Å—Ç–µ–º–Ω–æ–≥–æ –ø—Ä–æ–º–ø—Ç–∞, –≥–æ—Ç–æ–≤—ã–π –¥–ª—è –æ—Ç–ø—Ä–∞–≤–∫–∏ –≤ Telegram.`;

return {
  json: {
    chatInput: prompt,
    chat_id: $('Edit Fields').first().json.chat_id
  }
};

const userData = $('Get User for Idle Check').first().json;
const allVMs = $('Get VMs for Idle Check').all();

const userVMs = allVMs.filter(vm => vm.json.user_id === userData.user_id);

// –û—Å–Ω–æ–≤–Ω–∞—è –∫–ª–∞—Å—Å–∏—Ñ–∏–∫–∞—Ü–∏—è –ø–æ –∫—Ä–∏—Ç–∏—á–Ω–æ—Å—Ç–∏
const catastrophic = []; // >90 –¥–Ω–µ–π, stopped —Å dataLoss –∏–ª–∏ failed
const critical = [];     // 30-90 –¥–Ω–µ–π –∏–ª–∏ stopped
const high = [];         // 14-30 –¥–Ω–µ–π –∏–ª–∏ idle —Å –Ω–∏–∑–∫–∏–º usage
const medium = [];       // 7-14 –¥–Ω–µ–π
const normal = [];       // 3-7 –¥–Ω–µ–π
const low = [];          // 1-3 –¥–Ω—è
const minimal = [];      // <1 –¥–Ω—è

// –ú–æ–Ω–∏—Ç–æ—Ä–∏–Ω–≥ –≤ —Ä–µ–∞–ª—å–Ω–æ–º –≤—Ä–µ–º–µ–Ω–∏ –¥–ª—è –∞–∫—Ç–∏–≤–Ω—ã—Ö –í–ú
const realTimeClassification = {
  active: [],        // –¢–µ–∫—É—â–∞—è –∞–∫—Ç–∏–≤–Ω–∞—è —Å–µ—Å—Å–∏—è (active_session = true)
  recent: [],        // <15 –º–∏–Ω—É—Ç (0.01 –¥–Ω—è)
  today: [],         // <24 —á–∞—Å–∞ (<1 –¥–Ω—è)
  thisWeek: [],      // <7 –¥–Ω–µ–π (—É–∂–µ –≤ –æ—Å–Ω–æ–≤–Ω—ã—Ö –∫–∞—Ç–µ–≥–æ—Ä–∏—è—Ö)
};

// –ü–æ–ª—É—á–∞–µ–º —Ç–µ–∫—É—â–µ–µ –≤—Ä–µ–º—è –¥–ª—è —Ä–∞—Å—á—ë—Ç–∞ —Ç–æ—á–Ω–æ–≥–æ –ø—Ä–æ—Å—Ç–æ—è
const now = new Date();

userVMs.forEach(vm => {
  const vmData = vm.json;
  const inactivity = vmData.inactivity_days;
  const status = vmData.status;
  const usage = vmData.usage_hours_week;
  const lastActivity = vmData.last_activity_time ? new Date(vmData.last_activity_time) : null;
  const hasActiveSession = vmData.active_session === true;

  // –†–∞—Å—Å—á–∏—Ç—ã–≤–∞–µ–º —Ç–æ—á–Ω–æ–µ –≤—Ä–µ–º—è –ø—Ä–æ—Å—Ç–æ—è –≤ —á–∞—Å–∞—Ö/–º–∏–Ω—É—Ç–∞—Ö
  let exactInactivityHours = inactivity * 24;
